import java.awt.Graphics;
import java.io.File;
import java.io.IOException;

import javax.imageio.ImageIO;

/**
 * @author Henrik
 * A pisztoly kirajzolását valósitja meg
 */
public class DrawPisztoly extends Drawable {

	/**
	 * Betölti a pisztoly képét
	 */
	public DrawPisztoly() 
	{
		try {
			img=ImageIO.read(new File("./Jegmezo/image/gun.png"));
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
	}
	
	/**
	 * Kirajzolja a pisztoly képét
	 */
	public void draw(Graphics g) {
		
		g.drawImage(img, getPositionX()*80, getPositionY()*80+40, null);
	}
	
}
