import java.awt.Graphics;
import java.io.File;
import java.io.IOException;

import javax.imageio.ImageIO;

/**
 * @author Henrik
 * A viz kirajzolását valósitja meg
 */
public class DrawViz extends Drawable {

	/**
	 * Betölti a víz képét
	 */
	public DrawViz() 
	{
		try 
		{
			img=ImageIO.read(new File("./Jegmezo/image/water1.png"));
		} catch (IOException e) 
		{
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
	}
	
	/**
	 * kirajzolja a vizet
	 */
	public void draw(Graphics g) 
	{
		g.drawImage(img, getPositionX()*80, getPositionY()*80, null);
	}
	
}
